import React, { createContext, useState, useContext } from 'react';

const GameContext = createContext();

export const useGames = () => {
  const context = useContext(GameContext);
  if (!context) {
    throw new Error('useGames debe ser usado dentro de un GameProvider');
  }
  return context;
};

export const GameProvider = ({ children }) => {
  const [games] = useState([
    {
      _id: 'game1',
      nombre_juego: 'Super Mario Odyssey',
      precio: 1300,
      platform: 'Nintendo Switch',
      image: 'https://tse4.mm.bing.net/th/id/OIP.Z52BIGCQi2mFHam42kxmpQHaEK?cb=ucfimg2ucfimg=1&rs=1&pid=ImgDetMain&o=7&rm=3',
      badge: 'Más vendido'
    },
    {
      _id: 'game2',
      nombre_juego: 'Halo Infinite',
      precio: 960,
      platform: 'Xbox Series X|S',
      image: 'https://tse1.mm.bing.net/th/id/OIP.O6SJocUjmbTMlAv7fd-zRgHaEK?cb=ucfimg2ucfimg=1&rs=1&pid=ImgDetMain&o=7&rm=3',
      badge: 'Oferta -20%'
    }
  ]);

  const value = {
    games
  };

  return (
    <GameContext.Provider value={value}>
      {children}
    </GameContext.Provider>
  );
};
