import React from 'react';
import { ShoppingCart, Trash2, Plus, Minus, CreditCard, ArrowLeft } from 'lucide-react';

// Simulaci√≥n de hooks (reemplaza con tus hooks reales)
const useCart = () => ({
  cartItems: [
    {
      _id: '1',
      nombre_juego: 'Cyberpunk 2077',
      platform: 'PC',
      precio: 1299,
      image: 'https://images.unsplash.com/photo-1542751371-adc38448a05e?w=400&h=500&fit=crop',
      quantity: 1
    },
    {
      _id: '2',
      nombre_juego: 'The Witcher 3',
      platform: 'PlayStation 5',
      precio: 899,
      image: 'https://images.unsplash.com/photo-1511512578047-dfb367046420?w=400&h=500&fit=crop',
      quantity: 2
    }
  ],
  removeFromCart: (id) => console.log('Remove:', id),
  updateQuantity: (id, qty) => console.log('Update:', id, qty),
  totalPrice: 3097,
  totalItems: 3
});

const useAuth = () => ({ isAuthenticated: true });

const CartPage = () => {
  const { cartItems, removeFromCart, updateQuantity, totalPrice, totalItems } = useCart();
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return (
      <section className="section container">
        <div className="auth-required">
          <div className="auth-required-content">
            <ShoppingCart size={64} color="#ff2d55" strokeWidth={1.5} />
            <h2>Carrito de Compras</h2>
            <p>Debes iniciar sesi√≥n para ver tu carrito</p>
            <button className="btn btn--primary" style={{ marginTop: '1rem' }}>
              Iniciar Sesi√≥n
            </button>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="section container">
      <div className="cart-header">
        <button className="btn btn--ghost">
          <ArrowLeft size={20} />
          Seguir comprando
        </button>
        <h2>
          <ShoppingCart size={28} style={{ verticalAlign: 'middle', marginRight: '0.5rem' }} />
          Tu Carrito
        </h2>
        <div className="cart-badge">{totalItems} {totalItems === 1 ? 'art√≠culo' : 'art√≠culos'}</div>
      </div>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <ShoppingCart size={80} color="#9aa3af" strokeWidth={1} />
          <h3>Tu carrito est√° vac√≠o</h3>
          <p>Explora nuestra tienda y encuentra juegos incre√≠bles</p>
          <button className="btn btn--primary" style={{ marginTop: '1.5rem' }}>
            Explorar Cat√°logo
          </button>
        </div>
      ) : (
        <div className="cart-layout">
          <div className="cart-items-section">
            {cartItems.map(item => (
              <div key={item._id} className="cart-item-enhanced">
                <div className="cart-item-image">
                  <img src={item.image} alt={item.nombre_juego} />
                </div>
                
                <div className="cart-item-details">
                  <h3>{item.nombre_juego}</h3>
                  <p className="platform-tag">{item.platform}</p>
                  <div className="item-price-mobile">${item.precio.toLocaleString()} MXN</div>
                </div>

                <div className="cart-item-quantity">
                  <button 
                    className="quantity-btn"
                    onClick={() => updateQuantity(item._id, item.quantity - 1)}
                    disabled={item.quantity <= 1}
                  >
                    <Minus size={16} />
                  </button>
                  <span className="quantity-display">{item.quantity}</span>
                  <button 
                    className="quantity-btn"
                    onClick={() => updateQuantity(item._id, item.quantity + 1)}
                  >
                    <Plus size={16} />
                  </button>
                </div>

                <div className="cart-item-price">
                  ${(item.precio * item.quantity).toLocaleString()} MXN
                </div>

                <button 
                  className="cart-item-remove"
                  onClick={() => removeFromCart(item._id)}
                  title="Eliminar"
                >
                  <Trash2 size={18} />
                </button>
              </div>
            ))}
          </div>

          <div className="cart-summary">
            <div className="summary-card">
              <h3>Resumen del Pedido</h3>
              
              <div className="summary-row">
                <span>Subtotal ({totalItems} {totalItems === 1 ? 'art√≠culo' : 'art√≠culos'})</span>
                <span>${totalPrice.toLocaleString()} MXN</span>
              </div>
              
              <div className="summary-row">
                <span>Descuento</span>
                <span className="discount">-$0 MXN</span>
              </div>
              
              <div className="summary-divider"></div>
              
              <div className="summary-row summary-total">
                <span>Total</span>
                <span>${totalPrice.toLocaleString()} MXN</span>
              </div>

              <button className="btn btn--primary btn-checkout">
                <CreditCard size={20} />
                Proceder al Pago
              </button>

              <div className="payment-info">
                <p>üîí Pago seguro y encriptado</p>
                <p>‚úì Garant√≠a de devoluci√≥n de 14 d√≠as</p>
              </div>
            </div>
          </div>
        </div>
      )}

      <style>{`
        .cart-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 2rem;
          flex-wrap: wrap;
          gap: 1rem;
        }

        .cart-header h2 {
          margin: 0;
          font-size: 2rem;
          color: #f0f0f0;
        }

        .cart-badge {
          background: var(--bg-card);
          padding: 0.5rem 1rem;
          border-radius: 20px;
          font-size: 0.9rem;
          color: var(--muted);
          border: 1px solid var(--border);
        }

        .auth-required {
          min-height: 60vh;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .auth-required-content {
          text-align: center;
          padding: 3rem;
          background: var(--bg-card);
          border-radius: 16px;
          border: 1px solid var(--border);
          max-width: 500px;
        }

        .auth-required-content h2 {
          margin: 1rem 0 0.5rem;
          color: #f0f0f0;
        }

        .auth-required-content p {
          color: var(--muted);
          margin-bottom: 0;
        }

        .empty-cart {
          text-align: center;
          padding: 4rem 2rem;
          background: var(--bg-card);
          border-radius: 16px;
          border: 1px solid var(--border);
        }

        .empty-cart h3 {
          margin: 1rem 0 0.5rem;
          color: #f0f0f0;
        }

        .empty-cart p {
          color: var(--muted);
        }

        .cart-layout {
          display: grid;
          grid-template-columns: 1fr 400px;
          gap: 2rem;
        }

        .cart-items-section {
          display: flex;
          flex-direction: column;
          gap: 1rem;
        }

        .cart-item-enhanced {
          display: grid;
          grid-template-columns: 120px 1fr auto auto auto;
          gap: 1.5rem;
          align-items: center;
          background: var(--bg-card);
          padding: 1.5rem;
          border-radius: 16px;
          border: 1px solid var(--border);
          transition: 0.2s;
        }

        .cart-item-enhanced:hover {
          border-color: var(--primary);
        }

        .cart-item-image {
          width: 120px;
          height: 160px;
          border-radius: 12px;
          overflow: hidden;
        }

        .cart-item-image img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .cart-item-details {
          flex: 1;
        }

        .cart-item-details h3 {
          margin: 0 0 0.5rem;
          font-size: 1.1rem;
          color: #f0f0f0;
        }

        .platform-tag {
          display: inline-block;
          background: rgba(255, 92, 147, 0.15);
          color: var(--accent);
          padding: 0.3rem 0.8rem;
          border-radius: 6px;
          font-size: 0.8rem;
          font-weight: 600;
          margin: 0;
        }

        .item-price-mobile {
          display: none;
          color: var(--primary);
          font-weight: 700;
          margin-top: 0.8rem;
        }

        .cart-item-quantity {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          background: var(--bg-secondary);
          padding: 0.4rem;
          border-radius: 12px;
          border: 1px solid var(--border);
        }

        .quantity-btn {
          background: transparent;
          border: none;
          color: var(--txt);
          cursor: pointer;
          padding: 0.4rem 0.6rem;
          border-radius: 8px;
          display: flex;
          align-items: center;
          transition: 0.2s;
        }

        .quantity-btn:hover:not(:disabled) {
          background: var(--bg-card);
          color: var(--primary);
        }

        .quantity-btn:disabled {
          opacity: 0.4;
          cursor: not-allowed;
        }

        .quantity-display {
          min-width: 30px;
          text-align: center;
          font-weight: 600;
          color: var(--txt);
        }

        .cart-item-price {
          font-size: 1.2rem;
          font-weight: 700;
          color: var(--primary);
          min-width: 140px;
          text-align: right;
        }

        .cart-item-remove {
          background: transparent;
          border: 1px solid var(--border);
          color: var(--muted);
          cursor: pointer;
          padding: 0.6rem;
          border-radius: 10px;
          display: flex;
          align-items: center;
          transition: 0.2s;
        }

        .cart-item-remove:hover {
          background: rgba(255, 30, 30, 0.1);
          border-color: var(--danger);
          color: var(--danger);
        }

        .cart-summary {
          position: sticky;
          top: 90px;
          height: fit-content;
        }

        .summary-card {
          background: var(--bg-card);
          padding: 1.5rem;
          border-radius: 16px;
          border: 1px solid var(--border);
        }

        .summary-card h3 {
          margin: 0 0 1.5rem;
          color: #f0f0f0;
          font-size: 1.3rem;
        }

        .summary-row {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 1rem;
          color: var(--muted);
        }

        .summary-row span:last-child {
          font-weight: 600;
          color: var(--txt);
        }

        .discount {
          color: var(--success) !important;
        }

        .summary-divider {
          height: 1px;
          background: var(--border);
          margin: 1.5rem 0;
        }

        .summary-total {
          font-size: 1.3rem;
          margin-bottom: 1.5rem;
        }

        .summary-total span {
          color: var(--txt);
          font-weight: 700;
        }

        .summary-total span:last-child {
          color: var(--primary);
        }

        .btn-checkout {
          width: 100%;
          padding: 1rem;
          font-size: 1rem;
          justify-content: center;
        }

        .payment-info {
          margin-top: 1.5rem;
          padding-top: 1.5rem;
          border-top: 1px solid var(--border);
        }

        .payment-info p {
          font-size: 0.85rem;
          color: var(--muted);
          margin: 0.5rem 0;
          display: flex;
          align-items: center;
          gap: 0.5rem;
        }

        @media (max-width: 1024px) {
          .cart-layout {
            grid-template-columns: 1fr;
          }

          .cart-summary {
            position: static;
          }
        }

        @media (max-width: 768px) {
          .cart-header {
            justify-content: center;
          }

          .cart-item-enhanced {
            grid-template-columns: 80px 1fr;
            gap: 1rem;
            padding: 1rem;
          }

          .cart-item-image {
            width: 80px;
            height: 110px;
          }

          .cart-item-details {
            grid-column: 2;
          }

          .item-price-mobile {
            display: block;
          }

          .cart-item-quantity {
            grid-column: 1 / -1;
            justify-content: center;
          }

          .cart-item-price {
            display: none;
          }

          .cart-item-remove {
            grid-column: 1 / -1;
            justify-content: center;
          }
        }
      `}</style>
    </section>
  );
};

export default CartPage;
