import React from 'react';
import { useNavigate } from 'react-router-dom'; // Agrega este import
import { useCart } from '../../context/CartContext';
import { useAuth } from '../../context/AuthContext';

const GameCard = ({ game }) => {
  const { addToCart } = useCart();
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate(); // Agrega este hook

  const handleCardClick = () => {
    navigate(`/producto/${game._id}`);
  };

  const handleAddToCart = (e) => {
    e.stopPropagation(); // Evita que el click se propague al card
    if (!isAuthenticated) {
      alert('Debes iniciar sesi√≥n para agregar productos al carrito');
      return;
    }
    addToCart(game);
  };

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push('‚òÖ');
    }
    if (hasHalfStar) {
      stars.push('¬Ω');
    }
    while (stars.length < 5) {
      stars.push('‚òÜ');
    }

    return stars.join('');
  };

  return (
    <div className="card">
      {game.badge && (
        <div className="card__badge">{game.badge}</div>
      )}
      <img src={game.image} alt={game.nombre_juego} />
      <div className="card__body">
        <div className="card__platform">{game.platform}</div>
        <h3 className="card__title">{game.nombre_juego}</h3>
        
        <div className="card__rating">
          <span className="stars" style={{ color: '#ffb800' }}>
            {renderStars(game.rating)}
          </span>
          <span>({game.rating})</span>
        </div>

        <div className="card__price">
          <div>
            <div className="price">${game.precio} MXN</div>
            {game.precioOriginal && (
              <div className="price--old">${game.precioOriginal} MXN</div>
            )}
          </div>
        </div>

        <div className="card__actions">
          <button 
            className="btn btn--primary" 
            style={{ flex: 1 }}
            onClick={handleAddToCart}
          >
            üõí Agregar
          </button>
          <button className="btn btn--ghost">‚ù§Ô∏è</button>
        </div>
      </div>
    </div>
    
  );
};

export default GameCard;
